mapper.select:
SELECT 

FROM tb_icitem a

WHERE a.id = #{id}

SELECT
FROM tb_icitem a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND (b.id = #{classId.id} OR b.parent_ids LIKE '%,'||#{classId.id}||',%') CONCAT('%,', #{classId.id}, ',%')) '%'+#{classId.id}+'%')
AND a.f_number LIKE '%'||#{number}||'%' '%'+#{number}+'%' concat('%',#{number},'%')
AND a.f_name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.f_model LIKE '%'||#{model}||'%' '%'+#{model}+'%' concat('%',#{model},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC
LIMIT #{startPage},#{endPage}

SELECT 

FROM tb_icitem a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM tb_icitem


		WHERE id = #{id}


		WHERE f_classid = #{classId}

UPDATE tb_icitem SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE f_classid = #{classId}
