mapper.select:
SELECT 

FROM tb_news a

WHERE a.id = #{id}

SELECT 

FROM tb_news a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.f_headline DESC, a.update_date DESC

SELECT

FROM tb_news a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}
	and a.f_ispublic = 1



		ORDER BY ${page.orderBy}


		ORDER BY a.f_headline DESC, a.update_date DESC

SELECT 

FROM tb_news a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_news SET
	f_readcount = f_readcount + 1
WHERE id = #{id}

UPDATE tb_news SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
