mapper.select:
SELECT 

FROM tb_newspush a

WHERE a.id = #{id}

SELECT 

FROM tb_newspush a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_newspush a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		and a.f_newsid = #{newsId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
