mapper.select:
SELECT 

FROM ic_order_approve a

WHERE a.id = #{id}

SELECT
FROM ic_order_approve a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.f_type = #{type}
AND a.f_approval_emplid LIKE '%'||#{approvalEmplId}||'%' '%'+#{approvalEmplId}+'%' concat('%',#{approvalEmplId},'%')
AND a.f_name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ic_order_approve a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ic_order_approve SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
