mapper.select:
SELECT 

FROM ic_sobill a

WHERE a.id = #{id}

		SELECT 

		FROM ic_sobill a


			a.del_flag = #{DEL_FLAG_NORMAL}
			${dataScope}

				AND a.f_billno = #{billNo}


				AND a.f_synstatus = #{synStatus}


				AND cus.f_name LIKE
					'%'||#{cusName}||'%'
					'%'+#{cusName}+'%'
					concat('%',#{cusName},'%')


				AND emp.name LIKE
					'%'||#{empName}||'%'
					'%'+#{empName}+'%'
					concat('%',#{empName},'%')


				AND a.f_needtime BETWEEN #{beginNeedTime} AND #{endNeedTime}


				AND a.f_cancellation = #{cancellation}


				AND a.f_checkstatus = #{checkStatus}



                AND a.f_emplid = #{emplId}



                AND a.create_date < CURDATE()



                AND a.create_date  >=  #{startTime}



                AND a.create_date  <=  #{endTime}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC




			LIMIT #{startPage},#{endPage}

SELECT 

FROM ic_sobill a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ic_sobill SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT (SELECT approve.f_type FROM ic_order_approve approve WHERE approve.del_flag = '0' AND approve.f_sobillid = a.id ORDER BY approve.f_status DESC,approve.f_index DESC LIMIT 0,1) AS "approveType", (SELECT approve.f_status FROM ic_order_approve approve WHERE approve.del_flag = '0' AND approve.f_sobillid = a.id ORDER BY approve.f_status DESC,approve.f_index DESC LIMIT 0,1) AS "approveStatus", (SELECT approve.f_islast FROM ic_order_approve approve WHERE approve.del_flag = '0' AND approve.f_sobillid = a.id ORDER BY approve.f_status DESC,approve.f_index DESC LIMIT 0,1) AS "isLast", (SELECT date_format(approve.create_date, '%Y-%m-%d %H:%m:%s') FROM ic_order_approve approve WHERE approve.del_flag = '0' AND approve.f_sobillid = a.id ORDER BY approve.f_index ASC LIMIT 0,1) AS "initiateDateStr",
FROM ic_sobill a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.id IN (SELECT approve.f_sobillid FROM ic_order_approve approve WHERE approve.del_flag = '0')
AND a.f_emplid = #{emplId}
AND cus.f_name LIKE '%'||#{cusName}||'%' '%'+#{cusName}+'%' concat('%',#{cusName},'%')
LIMIT #{startPage},#{endPage}
