mapper.select:
SELECT 

FROM ic_sobillentry a

WHERE a.id = #{id}

SELECT
FROM ic_sobillentry a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.f_sobillid = #{sobillId}
AND a.f_itemid LIKE '%'||#{itemId}||'%' '%'+#{itemId}+'%' concat('%',#{itemId},'%')
AND a.f_synstatus = #{synStatus}
AND a.f_custid LIKE '%'||#{custId}||'%' '%'+#{custId}+'%' concat('%',#{custId},'%')
AND a.f_deptid LIKE '%'||#{deptId}||'%' '%'+#{deptId}+'%' concat('%',#{deptId},'%')
AND a.f_emplid LIKE '%'||#{emplId}||'%' '%'+#{emplId}+'%' concat('%',#{emplId},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date ASC

SELECT 

FROM ic_sobillentry a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
DELETE FROM ic_sobillentry


		WHERE id = #{id}


		WHERE f_sobillid = #{sobillId}

UPDATE ic_sobillentry SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE f_sobillid = #{sobillId}
