mapper.select:
SELECT 

FROM tb_warehouse a

WHERE a.id = #{id}

SELECT
FROM tb_warehouse a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.f_number = #{number}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY a.sort ASC

SELECT 

FROM tb_warehouse a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM tb_warehouse a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE tb_warehouse SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
